package charanpkg;

public class booleancls {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean a=true;
		boolean b=false;
		if (a==true)
			System.out.println(true);
		else
			System.out.println(false);
		if (b==false)
			System.out.println(false);
		else
			System.out.println(true);
		if (a)
			System.out.println(true);
		else
			System.out.println(false);
		if (!b)
			System.out.println(false);
		else
			System.out.println(true);
		

	}

}
