package charanpkg;

public class whileloopcls {
	public void disp(){
		int i=0;
		while (i<10){
			System.out.println(i);
			i++;
		}

		
	}
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		whileloopcls obj =new whileloopcls();
		obj.disp();
	}

}
